-- ----------------------------
-- Table structure for sys_spec_item
-- ----------------------------
DROP TABLE IF EXISTS `sys_spec_item`;
CREATE TABLE `sys_spec_item` (
  `spec_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '配置项ID',
  `menu_id` bigint(20) DEFAULT NULL COMMENT '配置所属菜单ID',
  `spec_code` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '配置项代码',
  `spec_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '配置项名称',
  `para` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '参数',
  `para_value` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci COMMENT '参数值',
  `param_type` int(11) DEFAULT NULL COMMENT '参数类型',
  `category` tinyint(1) DEFAULT '0' COMMENT '配置类别 0-系统 1-模块',
  `order_num` tinyint(1) DEFAULT '0' COMMENT '配置顺序',
  `is_hide` int(11) DEFAULT '1' COMMENT '是否隐藏(0、是 1、否 )',
  `create_user_id` bigint(20) DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modified_user_id` bigint(20) DEFAULT NULL COMMENT '修改人ID',
  `modified_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `is_delete` int(11) DEFAULT '1' COMMENT '逻辑删除（0、删除 1、正常）',
  `option1` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '扩展字段1',
  `option2` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '扩展字段2',
  `option3` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '扩展字段3',
  PRIMARY KEY (`spec_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=150 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC COMMENT='系统配置表';

