mapper.select:
select sr.role_id, sr.role_code, sr.role_name, sr.role_desc, sr.create_user_id, u.user_name as create_user_name, sr.create_time, sr.modified_user_id, s.user_name as modified_user_name, sr.modified_time from sys_role sr
LEFT JOIN sys_user_role sur ON sur.role_id = sr.role_id
left join sys_user u on sr.create_user_id=u.user_id left join sys_user s on sr.modified_user_id=s.user_id
1=1
and sr.role_name like CONCAT('%', #{roleName}, '%')
and sr.role_desc like CONCAT('%', #{roleDesc}, '%')
and sr.status=#{status}
and sur.user_id =#{userId}
and sr.role_code=#{roleCode}

select sr.role_id,sr.role_code, sr.role_name, sr.role_desc, sr.create_user_id, sr.create_time, sr.modified_user_id, sr.modified_time,sr.status, sr.is_delete,sar.menu_id from sys_auth_role sar left join sys_role sr on sar.role_id = sr.role_id
#{item}
group by sar.menu_id

select sr.role_id, sr.role_code, sr.role_name, sr.role_desc, sr.create_user_id, u.user_name as create_user_name, sr.create_time, sr.modified_user_id, s.user_name as modified_user_name, sr.modified_time from sys_role sr left join sys_user u on sr.create_user_id=u.user_id left join sys_user s on sr.modified_user_id=s.user_id left join sys_user_role sur on sr.role_id=sur.role_id left join sys_user su on sur.user_id=su.user_id
1=1
and su.user_name=#{userName}
and su.user_name=#{userName}
and sr.status=#{status}

select sr.* from sys_auth_role sar
left join sys_role sr on sr.role_id = sar.role_id
where sar.menu_id = #{menuId}
