mapper.select:
select swo.work_order_id,swo.work_order_code,swo.order_id,swo.material_id,sm.material_code,sm.material_name,sm.material_version,sm.material_desc,bt.transfer_quantity, swo.work_order_quantity,swo.production_quantity,swo.output_quantity,swo.scheduled_quantity,swo.work_order_status,swo.pro_line_id,spl.pro_name,swo.route_id,sr.route_name, swo.work_order_type,swo.planned_start_time,swo.planned_end_time,swo.actual_start_time,swo.actual_end_time,so.order_code, swo.is_delete, swo.contract_no,bt.color,spm.product_model_name,br.barcode_rule_id,br.barcode_rule
,wobp.work_order_card_id as barcode
from fantech_imes_v2.smt_work_order swo left join fantech_imes_v2.base_material sm on swo.material_id=sm.material_id LEFT JOIN base_material_tab bt on bt.material_id = sm.material_id left join base_product_model spm on spm.product_model_id = bt.product_model_id left join fantech_imes_v2.base_route sr on swo.route_id=sr.route_id left join fantech_imes_v2.base_pro_line spl on swo.pro_line_id=spl.pro_line_id
left join fantech_imes_v2.base_barcode_rule_set_det wobp on wobp.barcode_rule_set_id = swo.barcode_rule_set_id LEFT JOIN base_barcode_rule br ON wobp.barcode_rule_id = br.barcode_rule_id
LEFT JOIN base_work_order_card_pool wobp ON sw.work_order_id = wobp.work_order_id
left join base_order so on so.order_id = swo.order_id
and swo.work_order_code = #{workOrderCode}
and bl.label_name =#{labelName}
and bl.print_mode = #{printMode}
and bl.save_path =#{savePath}
and br.barcode_rule_category_id=1

SELECTbl.print_mode,bl.save_path,su.user_name as
create_user_name,sus.user_name as modified_user_name, bo.organization_name
FROM base_bar_code bc
LEFT JOIN bcm_label_material blm ON bc.label_material_id = blm.label_material_id
LEFT JOIN bcm_label bl ON blm.label_id = bl.label_id
LEFT JOIN base_work_order sw on bc.work_order_id = sw.work_order_id
LEFT JOIN ocean_v2.sys_user su ON bl.create_user_id = su.user_id
LEFT JOIN ocean_v2.sys_user sus ON bl.modified_user_id = sus.user_id
LEFT JOIN fantech_imes_v2.base_organization bo ON bc.org_id = bo.organization_id


        and sw.work_order_code = #{workOrderCode}


        and bl.label_name =#{labelName}


        and bl.print_mode = #{printMode}


        and bl.save_path =#{savePath}


        and bc.bar_code_type = #{barCodeType}


        and bc.org_id=#{orgId}

SELECT bc.bar_code_id,swo.work_order_code,bc.print_quantity FROM bcm_bar_code bc LEFT JOIN base_work_order swo ON swo.work_order_id = bc.work_order_id
bc.status=2
and swo.work_order_id = #{workOrderId}
