mapper.select:
select , bmo.material_owner_name,bw.warehouse_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from base_ht_inventory_status bhis left join base_material_owner bmo on bhis.material_owner_id=bmo.material_owner_id left join base_warehouse bw on bhis.warehouse_id=bw.warehouse_id left join sys_user u on bhis.create_user_id=u.user_id left join sys_user s on bhis.modified_user_id=s.user_id left join base_organization bo on bhis.org_id = bo.organization_id
and bhis.warehouse_id = #{warehouseId}
and bhis.material_owner_id = #{materialOwnerId}
and bmo.material_owner_name like CONCAT('%', #{materialOwnerName}, '%')
and bw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and bhis.inventory_status_name like CONCAT('%', #{inventoryStatusName}, '%')
and bhis.status=#{status}
and date_format(bhis.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and bhis.org_id=#{orgId}
order by bhis.create_time desc
