mapper.select:
SELECT, bm.material_code,bm.material_name,bm.material_desc,bm.material_version,bmt.material_property, su.user_name as create_user_name, sus.user_name as modified_user_name, bo.organization_name FROM base_ht_production_key_issues bhpki left join base_material bm on bhpki.material_id=bm.material_id left join base_material_tab bmt on bm.material_id=bmt.material_id LEFT JOIN sys_user su ON bhpki.create_user_id = su.user_id LEFT JOIN sys_user sus ON bhpki.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON bhpki.org_id = bo.organization_id
and bm.material_code like CONCAT('%', #{materialCode}, '%')
and bm.material_desc like CONCAT('%', #{materialDesc}, '%')
and bhpki.key_issues_type = #{keyIssuesType}
and bhpki.status = #{status}
and date_format(bhpki.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and bhpki.org_id=#{orgId}
order by bhpki.create_time desc
