mapper.select:
SELECT,sw.warehouse_name as 'warehouseName',smc.material_category_name as 'materialCategoryName',sm.material_code as 'materialCode', sm.material_name as 'materialName',sm.material_desc as 'materialDesc',sm.material_version as 'materialVersion',bmo.material_owner_name as 'materialOwnerName', su.user_name create_user_name,sus.user_name as modified_user_name, bo.organization_name FROM base_ht_safe_stock os LEFT JOIN base_material_owner bmo ON os.material_owner_id=bmo.material_owner_id LEFT JOIN base_warehouse sw ON os.warehouse_id=sw.warehouse_id LEFT JOIN base_material_category smc ON os.material_category_id = smc.material_category_id LEFT JOIN base_material sm ON os.material_id = sm.material_id LEFT JOIN sys_user su ON os.create_user_id = su.user_id LEFT JOIN sys_user sus ON os.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON os.org_id = bo.organization_id
and sw.warehouse_id = #{warehouseId}
and sw.warehouse_name like CONCAT('%',#{warehouseName},'%')
and bmo.material_owner_id = #{materialOwnerId}
and bmo.material_owner_name like CONCAT('%',#{materialOwnerName},'%')
and sm.material_id = #{materialId}
and sm.material_code like CONCAT('%',#{materialCode},'%')
and sm.material_name like CONCAT('%',#{materialName},'%')
and sm.material_desc like CONCAT('%',#{materialDesc},'%')
and os.status=#{status}
and date_format(os.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and os.org_id=#{orgId}
order by os.create_time desc
