mapper.select:
select , sp.sample_plan_code,bstr.sample_transition_rule_code, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.base_ht_sample_process bhsp left join fantech_imes_v2.base_sample_plan sp on bhsp.sample_plan_id=sp.sample_plan_id left join fantech_imes_v2.base_sample_transition_rule bstr on bhsp.sample_transition_rule_id=bstr.sample_transition_rule_id left join ocean_v2.sys_user u on bhsp.create_user_id=u.user_id left join ocean_v2.sys_user s on bhsp.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo on bhsp.org_id = bo.organization_id
and bhsp.sample_process_code like CONCAT('%', #{sampleProcessCode}, '%')
and bhsp.sample_process_desc like CONCAT('%', #{sampleProcessDesc}, '%')
and bhsp.sample_process_type = #{sampleProcessCode}
and bhsp.status=#{status}
and date_format(bhsp.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by bhsp.modified_time desc
