mapper.select:
select ,
u.user_name as create_user_name, s.user_name as modified_user_name,
bo.organization_name
from base_ht_supplier_re_user bhsru
left join sys_user u on bhsru.create_user_id=u.user_id
left join sys_user s on bhsru.modified_user_id=s.user_id
LEFT JOIN base_organization bo ON bhsru.organization_id = bo.organization_id


    and bhsru.user_id  = #{userId}


    and bhsru.supplier_id = #{supplierId}


    and date_format(bhsru.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')


    and bhsru.status=#{status}


    and bhsru.organization_id=#{orgId}


order by create_time desc
