mapper.select:
select, su.user_name AS create_user_name, sus.user_name AS modified_user_name, sm.material_code,sm.material_name, spm.product_model_code,spm.product_model_name, sp.process_code,sp.process_name,sp.process_desc, bo.organization_name FROM base_ht_unit_price bhup LEFT JOIN base_material sm ON bhup.material_id = sm.material_id LEFT JOIN base_material_tab bt ON sm.material_id = bt.material_id LEFT JOIN base_product_model spm ON bt.product_model_id = spm.product_model_id LEFT JOIN base_process sp ON bhup.process_id = sp.process_id LEFT JOIN sys_user su on bhup.create_user_id = su.user_id LEFT JOIN sys_user sus on bhup.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON sm.org_id = bo.organization_id
and date_format(bhup.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and sm.material_code like CONCAT('%',#{materialCode},'%')
and sm.material_name like CONCAT('%',#{materialName},'%')
and sm.material_id = #{materialId}
and bhup.org_id=#{orgId}
