mapper.select:
select , bsp.sample_process_code,bii2.inspection_item_desc as inspection_item_desc_big, bii.inspection_item_desc as inspection_item_desc_small,bii.inspection_item_standard, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.base_inspection_standard_det bisd left join fantech_imes_v2.base_inspection_item bii on bisd.inspection_item_id=bii.inspection_item_id left join fantech_imes_v2.base_inspection_item bii2 on bii.parent_id=bii2.inspection_item_id left join fantech_imes_v2.base_sample_process bsp on bisd.sample_process_id=bsp.sample_process_id left join ocean_v2.sys_user u on bisd.create_user_id=u.user_id left join ocean_v2.sys_user s on bisd.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo on bisd.org_id = bo.organization_id
and bisd.inspection_standard_id = #{inspectionStandardId}
and bisd.status=#{status}
and date_format(bisd.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and bisd.org_id=#{orgId}
order by bisd.create_time desc
