mapper.select:
select , bmo.material_owner_name,bw.warehouse_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from base_inventory_status bis left join base_material_owner bmo on bis.material_owner_id=bmo.material_owner_id left join base_warehouse bw on bis.warehouse_id=bw.warehouse_id left join sys_user u on bis.create_user_id=u.user_id left join sys_user s on bis.modified_user_id=s.user_id left join base_organization bo on bis.org_id = bo.organization_id
and bis.inventory_status_id = #{inventoryStatusId}
and bis.warehouse_id = #{warehouseId}
and bis.material_owner_id = #{materialOwnerId}
and bmo.material_owner_name like CONCAT('%', #{materialOwnerName}, '%')
and bw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and bis.inventory_status_name like CONCAT('%', #{inventoryStatusName}, '%')
and bis.inventory_status_name = #{inventoryStatusName}
and bis.status=#{status}
and date_format(bis.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and bis.org_id=#{orgId}
and #FULL_FROM_QUERY
order by bis.create_time desc
