mapper.select:
select, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name from base_material sm left join sys_user su on sm.create_user_id=su.user_id left join sys_user sus on sm.modified_user_id=sus.user_id LEFT JOIN base_barcode_rule_set sbrs on sm.barcode_rule_set_id = sbrs.barcode_rule_set_id LEFT JOIN base_organization bo ON sm.org_id = bo.organization_id LEFT JOIN base_material_tab bt on sm.material_id = bt.material_id LEFT JOIN base_material_category bmc on bmc.material_category_id = bt.material_category_id
and bt.material_property=#{materialProperty}
and bt.material_property in(0,1)
and date_format(sm.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and sm.material_id =#{materialId}
and sm.material_code =#{materialCode}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_name like CONCAT('%', #{materialName}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and sm.material_id!=(select material_id from base_product_bom where product_bom_id=#{productBomId})
and sm.material_id!=(select material_id from base_work_order where work_order_id=#{workOrderId})
and sm.status=#{status}
and sm.org_id=#{organizationId}
and bt.material_category_id = #{materialCategoryId}
and bmc.material_category_code in
#{materialCategoryCode}
order by sm.create_time desc

select, su.user_name AS create_user_name, sus.user_name AS modified_user_name, bo.organization_name FROM wms_inner_stock_order_det det LEFT JOIN base_material sm ON det.material_id=sm.material_id LEFT JOIN sys_user su ON sm.create_user_id=su.user_id LEFT JOIN sys_user sus ON sm.modified_user_id=sus.user_id LEFT JOIN base_barcode_rule_set sbrs ON sm.barcode_rule_set_id = sbrs.barcode_rule_set_id LEFT JOIN base_organization bo ON sm.org_id = bo.organization_id LEFT JOIN base_material_tab bt ON sm.material_id = bt.material_id LEFT JOIN base_material_category bmc ON bmc.material_category_id = bt.material_category_id
and det.stock_order_id=#{sourceId}
and bt.material_property=#{materialProperty}
and bt.material_property in(0,1)
and date_format(sm.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and sm.material_id =#{materialId}
and sm.material_code =#{materialCode}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_name like CONCAT('%', #{materialName}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and sm.material_id!=(select material_id from base_product_bom where product_bom_id=#{productBomId})
and sm.material_id!=(select material_id from base_work_order where work_order_id=#{workOrderId})
and sm.status=#{status}
and sm.org_id=#{organizationId}
and bt.material_category_id = #{materialCategoryId}
and bmc.material_category_code in
#{materialCategoryCode}
group by sm.material_id

update.foreach:
update base_material
material_code =#{item.materialCode}, material_name =#{item.materialName}, material_desc =#{item.materialDesc}, material_version =#{item.materialVersion}, material_source =#{item.materialSource}, barcode_rule_set_id =#{item.barcodeRuleSetId}, status =#{item.status}, min_package_number =#{item.minPackageNumber}, create_user_id =#{item.createUserId}, create_time =#{item.createTime}, modified_user_id =#{item.modifiedUserId}, modified_time =#{item.modifiedTime}, is_delete =#{item.isDelete}, option1 =#{item.option1}, option2 =#{item.option2}, option3 =#{item.option3},
where material_code =#{item.materialCode}
