mapper.select:
SELECT, su.user_name AS create_user_name,
sus.user_name AS modified_user_name,
bo.organization_name,
bm.material_code,
bm.material_name,
bm.material_desc,
bs.supplier_name
FROM base_order_flow bof
LEFT JOIN sys_user su ON bof.create_user_id = su.user_id
LEFT JOIN sys_user sus ON bof.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON bof.org_id = bo.organization_id
LEFT JOIN base_material bm ON bof.material_id=bm.material_id
LEFT JOIN base_supplier bs ON bof.supplier_id=bs.supplier_id


    and date_format(bof.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and bof.operation_module = #{operationModule}


    and bof.business_type =#{businessType}


    and bof.order_flow_dimension = #{orderFlowDimension}


    and bof.material_id = #{materialId}


    and bof.supplier_id = #{supplierId}


    and bof.order_node = #{orderNode}


    and bof.order_type_code = #{orderTypeCode}


    and bof.source_order_type_code = #{sourceOrderTypeCode}


    and bof.next_order_type_code = #{nextOrderTypeCode}


    and bof.remark like CONCAT('%', #{remark}, '%')



    and bof.status=#{status}


    and bof.org_id=#{orgId}


order by bof.create_time desc

select *
from base_order_flow
where order_type_code = #{orderTypeCode}
and order_flow_dimension = 1
and status= 1
and org_id=#{orgId}

    union
    select *
    from base_order_flow
    where order_type_code = #{orderTypeCode}
    and order_flow_dimension = 2
    and supplier_id = #{supplierId}
    and status= 1
    and org_id=#{orgId}


    union
    select *
    from base_order_flow
    where order_type_code = #{orderTypeCode}
    and order_flow_dimension = 3
    and material_id = #{materialId}
    and status= 1
    and org_id=#{orgId}

ORDER BY order_flow_dimension DESC LIMIT 1
