mapper.select:
SELECT,
su.user_name AS create_user_name,
sus.user_name AS modified_user_name,
bo.organization_name
FROM base_order_flow_source_det bofsd
LEFT JOIN sys_user su ON bofsd.create_user_id = su.user_id
LEFT JOIN sys_user sus ON bofsd.modified_user_id = sus.user_id
LEFT JOIN base_organization bo ON bofsd.org_id = bo.organization_id


    and date_format(bofsd.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')


    and bofsd.order_flow_source_id = #{orderFlowSourceId}


    and bofsd.source_order_type_code = #{sourceOrderTypeCode}


    and bofsd.next_order_type_code = #{nextOrderTypeCode}


    and bofsd.status=#{status}


    and bofsd.org_id=#{orgId}


order by bofsd.create_time desc
