mapper.select:
select ,su.user_name AS create_user_name, sus.user_name AS modified_user_name,bo.organization_name, bpi.parts_information_code,bpi.parts_information_name,sr.route_name from base_plate_parts_det bppd LEFT JOIN sys_user su ON bppd.create_user_id = su.user_id LEFT JOIN sys_user sus ON bppd.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON bppd.org_id = bo.organization_id LEFT JOIN base_parts_information bpi ON bpi.parts_information_id = bppd.parts_information_id LEFT JOIN base_route sr ON sr.route_id = bppd.route_id
and bppd.plate_parts_det_id = #{platePartsDetId}
and bppd.plate_parts_id = #{platePartsId}
and bpi.parts_information_code like CONCAT('%', #{partsInformationName}, '%')
and bpi.parts_information_name like CONCAT('%', #{partsInformationCode}, '%')
and sr.route_id = #{routeId}
and sr.route_name like CONCAT('%', #{routeName}, '%')
and date_format(bppd.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and bppd.org_id=#{orgId}
order by create_time desc
