mapper.select:
select ,su.user_name AS create_user_name, sus.user_name AS modified_user_name,bo.organization_name, sm.material_code,sm.material_name,sm.material_desc,bt.material_quality,spm.product_model_name,bt.color,spm.product_model_name, sps.package_specification_code,sps.package_specification_name from base_plate_parts bpp LEFT JOIN sys_user su ON bpp.create_user_id = su.user_id LEFT JOIN sys_user sus ON bpp.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON bpp.org_id = bo.organization_id LEFT JOIN base_material sm ON sm.material_id = bpp.material_id LEFT JOIN base_material_tab bt ON bt.material_id = sm.material_id LEFT JOIN base_product_model spm ON spm.product_model_id = bt.product_model_id LEFT JOIN base_package_specification sps ON sps.package_specification_id = bt.package_specification_id
and bpp.plate_parts_id = #{platePartsId}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_id = #{materialId}
and sm.material_name like CONCAT('%', #{materialName}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and spm.product_model_name like CONCAT('%', #{productModelName}, '%')
and date_format(bpp.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and bpp.org_id=#{orgId}
and bpp.if_customized=#{ifCustomized}
order by create_time desc
