mapper.select:
select , sws.section_name as sectionName, spc.process_category_name as processCategoryName,spc.process_category_code as processCategoryCode, u.user_name as createUserName, s.user_name as modifiedUserName, bo.organization_name from base_process sp left join base_workshop_section sws on sp.section_id=sws.section_id left join base_process_category spc on sp.process_category_id=spc.process_category_id left join sys_user u on sp.create_user_id=u.user_id left join sys_user s on sp.modified_user_id=s.user_id LEFT JOIN base_organization bo ON sp.org_id = bo.organization_id
and sp.process_id=#{processId}
and date_format(sp.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and sp.process_code like CONCAT('%', #{processCode}, '%')
and sp.process_code = #{processCode}
and sp.process_name like CONCAT('%', #{processName}, '%')
and sp.process_desc like CONCAT('%', #{processDesc}, '%')
and sp.section_id=#{sectionId}
and sws.section_name like CONCAT('%', #{sectionName}, '%')
and sp.process_category_id=#{processCategoryId}
and spc.process_category_name like CONCAT('%', #{processCategoryName}, '%')
and spc.process_category_code = #{processCategoryCode}
and sp.status=#{status}
and sp.is_job_scan = #{isJobScan}
and sp.is_start_scan = #{isStartScan}
and sp.process_id IN (SELECT srp.process_id FROM base_route_process srp WHERE srp.route_id=#{routeId}) and sp.status=1
and sp.process_id NOT IN (SELECT srp.process_id FROM base_route_process srp WHERE srp.route_id=#{routeId}) and sp.status=1
and sp.process_id IN (SELECT bsp.process_id FROM base_staff_process bsp WHERE bsp.staff_id=#{staffId}) and sp.status=1
and sp.process_id NOT IN (SELECT bsp.process_id FROM base_staff_process bsp WHERE bsp.staff_id=#{staffId}) and sp.status=1
and sp.org_id=#{orgId}
and #FULL_FROM_QUERY
order by sp.create_time desc
