mapper.select:
select , sm.material_code as material_code,sm.material_name as material_name,sm.material_version as material_version , sm.material_desc as material_desc, sp.process_name,u.user_name as create_user_name,s.user_name as modified_user_name, bo.organization_name from base_product_bom_det spbd left join base_material sm on spbd.material_id=sm.material_id left join base_process sp on spbd.process_id=sp.process_id left join sys_user u on spbd.create_user_id=u.user_id left join sys_user s on spbd.modified_user_id=s.user_id LEFT JOIN base_organization bo ON spbd.org_id = bo.organization_id
and date_format(spbd.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and spbd.product_bom_id=#{productBomId}
and spbd.product_bom_det_id=#{productBomDetId}
and spbd.material_id=#{materialId}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and spbd.org_id=#{orgId}
order by spbd.create_time desc
