mapper.select:
select , bm.material_code,bm.material_desc,bm.material_version,sbm.material_code as sub_material_code,blc.label_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.base_product_material_re_p bpmrp left join fantech_imes_v2.base_label_category blc on bpmrp.label_category_id=blc.label_category_id left join fantech_imes_v2.base_material bm on bpmrp.material_id=bm.material_id left join fantech_imes_v2.base_material sbm on bpmrp.sub_material_id=sbm.material_id left join ocean_v2.sys_user u on bpmrp.create_user_id=u.user_id left join ocean_v2.sys_user s on bpmrp.modified_user_id=s.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON bpmrp.org_id = bo.organization_id
and date_format(bpmrp.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and bpmrp.product_process_re_m_id=#{productProcessReMId}
and bpmrp.status=#{status}
order by bpmrp.create_time desc
