mapper.select:
select , bp.process_code,bp.process_name,bp.process_desc, bws.section_name,bpc.process_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.base_product_process_re_m bpprm left join fantech_imes_v2.base_process bp on bpprm.process_id=bp.process_id left join fantech_imes_v2.base_workshop_section bws on bws.section_id=bp.section_id left join fantech_imes_v2.base_process_category bpc on bpc.process_category_id=bp.process_category_id left join ocean_v2.sys_user u on bpprm.create_user_id=u.user_id left join ocean_v2.sys_user s on bpprm.modified_user_id=s.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON bpprm.org_id = bo.organization_id
and date_format(bpprm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and bpprm.material_id=#{materialId}
and bpprm.status=#{status}
and bpprm.org_id=#{orgId}
order by bpprm.create_time desc
