mapper.select:
select , spl.pro_name, spl.pro_code, spm.product_model_code, spm.product_model_name, sm.material_code,sm.material_name,sm.material_desc,sm.material_version, sppr.route_id, bo.organization_name, sr.route_name, sr.route_code, sr.route_desc, u.user_name as create_user_name,s.user_name as modified_user_name from base_product_process_route sppr left join base_pro_line spl on sppr.pro_line_id=spl.pro_line_id left join base_product_model spm on sppr.product_model_id=spm.product_model_id left join base_material sm on sppr.material_id=sm.material_id left join sys_user u on sppr.create_user_id=u.user_id left join sys_user s on sppr.modified_user_id=s.user_id left join base_route sr on sppr.route_id = sr.route_id LEFT JOIN base_organization bo ON sppr.org_id = bo.organization_id
and date_format(sppr.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and sppr.product_type=#{productType}
and sppr.pro_line_id=#{proLineId}
and sppr.product_model_id=#{productModelId}
and sppr.material_id=#{materialId}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and spl.pro_name like CONCAT('%', #{proName}, '%')
and spm.product_model_name like CONCAT('%', #{productModelName}, '%')
and sppr.route_id=#{routeId}
and sr.route_name like CONCAT('%', #{routeName}, '%')
and sr.route_code like CONCAT('%', #{routeCode}, '%')
and sppr.status=#{status}
and sppr.org_id=#{orgId}
and #FULL_FROM_QUERY
order by sppr.create_time desc
