mapper.select:
SELECT ,sw.warehouse_name as 'warehouseName',smc.material_category_name as 'materialCategoryName',sm.material_code as 'materialCode', sm.material_name as 'materialName',sm.material_desc as 'materialDesc',sm.material_version as 'materialVersion',bmo.material_owner_name as 'materialOwnerName', su.user_name create_user_name,sus.user_name as modified_user_name, bo.organization_name FROM fantech_imes_v2.base_safe_stock os LEFT JOIN fantech_imes_v2.base_material_owner bmo ON os.material_owner_id=bmo.material_owner_id LEFT JOIN fantech_imes_v2.base_warehouse sw ON os.warehouse_id=sw.warehouse_id LEFT JOIN fantech_imes_v2.base_material_category smc ON os.material_category_id = smc.material_category_id LEFT JOIN fantech_imes_v2.base_material sm ON os.material_id = sm.material_id LEFT JOIN ocean_v2.sys_user su ON os.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON os.modified_user_id = sus.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON os.org_id = bo.organization_id
and sw.warehouse_id = #{warehouseId}
and sm.material_id = #{materialId}
and sm.material_desc like CONCAT('%',#{materialDesc},'%')
order by os.create_time desc

SELECT sum(ssi.quantity) FROM fantech_imes_v2.base_storage_inventory ssi
LEFT JOIN fantech_imes_v2.base_storage ss ON ssi.storage_id=ss.storage_id
LEFT JOIN fantech_imes_v2.base_warehouse_area wa ON ss.warehouse_area_id =wa.warehouse_area_id
LEFT JOIN fantech_imes_v2.base_warehouse sw ON wa.warehouse_id=sw.warehouse_id


    and sw.warehouse_id = #{warehouseId}


    and ssi.material_id =#{materialId}
