mapper.select:
select , sp.sample_plan_code,bstr.sample_transition_rule_code, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.base_sample_process bsp left join fantech_imes_v2.base_sample_plan sp on bsp.sample_plan_id=sp.sample_plan_id left join fantech_imes_v2.base_sample_transition_rule bstr on bsp.sample_transition_rule_id=bstr.sample_transition_rule_id left join ocean_v2.sys_user u on bsp.create_user_id=u.user_id left join ocean_v2.sys_user s on bsp.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo on bsp.org_id = bo.organization_id
and bsp.sample_process_code like CONCAT('%', #{sampleProcessCode}, '%')
and bsp.sample_process_desc like CONCAT('%', #{sampleProcessDesc}, '%')
and bsp.sample_process_type = #{sampleProcessCode}
and bsp.status=#{status}
and date_format(bsp.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
order by bsp.create_time desc
