mapper.select:
select , sm.material_code as materialCode, sm.material_name as materialName, sm.material_version as materialVersion, sm.material_desc as materialDesc, ssu.supplier_name as supplierName, u.user_name as createUserName, s.user_name as modifiedUserName, bo.organization_name from fantech_imes_v2.base_signature ss left join fantech_imes_v2.base_material sm on ss.material_id=sm.material_id left join fantech_imes_v2.base_supplier ssu on ss.supplier_id=ssu.supplier_id left join ocean_v2.sys_user u on ss.create_user_id=u.user_id left join ocean_v2.sys_user s on ss.modified_user_id=s.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON ss.org_id = bo.organization_id
and date_format(ss.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and ss.signature_code like CONCAT('%', #{signatureCode}, '%')
and ss.status=#{status}
order by ss.create_time desc
