mapper.select:
select ,
sw.warehouse_name, sw.warehouse_code,sw.warehouse_category,bwa.working_area_code,
u.user_name as createUserName, s.user_name as modifiedUserName,
swa.warehouse_area_code, swa.warehouse_area_name,
bo.organization_name
from fantech_imes_v2.base_storage ss
left join fantech_imes_v2.base_working_area bwa on ss.working_area_id=bwa.working_area_id
left join fantech_imes_v2.base_warehouse_area swa on bwa.warehouse_area_id=swa.warehouse_area_id
left join fantech_imes_v2.base_warehouse sw on swa.warehouse_id=sw.warehouse_id
left join ocean_v2.sys_user u on ss.create_user_id=u.user_id
left join ocean_v2.sys_user s on ss.modified_user_id=s.user_id
LEFT JOIN fantech_imes_v2.base_organization bo ON ss.org_id = bo.organization_id


        and date_format(ss.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


        and ss.storage_id = #{storageId}


        and ss.storage_type = #{storageType}


        and ss.storage_code = #{storageCode}


        and ss.storage_code like CONCAT('%', #{storageCode}, '%')


        and ss.storage_name like CONCAT('%', #{storageName}, '%')


        and ss.storage_desc like CONCAT('%', #{storageDesc}, '%')


        and ss.warehouse_id=#{warehouseId}


        and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')


        and ss.warehouse_area_Id=#{warehouseAreaId}


        and swa.warehouse_area_name like CONCAT('%', #{warehouseAreaName}, '%')


        and ss.status=#{status}


        and bwa.working_area_code like CONCAT('%', #{workingAreaCode}, '%')


        and ss.surplus_can_put_salver>#{minSurplusCanPutSalver}


        and ss.org_id=#{orgId}


order by ss.create_time desc

update.foreach:
update base_storage
storage_code =#{item.storageCode}, storage_name =#{item.storageName}, `level` =#{item.level}, storage_desc =#{item.storageDesc}, capacity =#{capacity}, temperature =#{temperature}, unit =#{unit}, roadway =#{roadway}, row_no =#{rowNo}, column_no =#{columnNo}, level_no =#{levelNo}, putaway_move_line_no =#{putawayMoveLineNo}, picking_move_line_no =#{pickingMoveLineNo}, stock_move_line_no =#{stockMoveLineNo}, if_stock_lock =#{ifStockLock}, surplus_load =#{surplusLoad}, surplus_volume =#{surplusVolume}, surplus_can_put_salver =#{surplusCanPutSalver}, warehouse_id =#{item.warehouseId}, warehouse_area_id =#{item.warehouseAreaId}, working_area_id =#{item.workingAreaId}, status =#{item.status}, create_user_id =#{item.createUserId}, create_time =#{item.createTime}, modified_user_id =#{item.modifiedUserId}, modified_time =#{item.modifiedTime}, is_delete =#{item.isDelete}, option1 =#{item.option1}, option2 =#{item.option2}, option3 =#{item.option3}
where storage_code =#{item.storageCode}

mapper.select:
SELECT bs.storage_id as storageId, bs.roadway, bs.row_no as rowNo, bs.column_no as columnNo, bs.level_no as levelNo, bs.putaway_move_line_no as putawayMoveLineNo, IFNULL(bs.surplus_load,0) as surplusLoad, IFNULL(bs.surplus_volume,0) as surplusVolume, bs.surplus_can_put_salver as surplusCanPutSalver, IFNULL(bmt.volume,0) as volume, IFNULL(bmt.net_weight,0) as netWeight FROM base_storage bs LEFT JOIN wms_inner_inventory wii ON bs.storage_id = wii.storage_id AND wii.packing_qty>0 LEFT JOIN base_material_tab bmt ON wii.material_id = bmt.material_id
bs.storage_type=2
and wii.warehouse_id =#{warehouseId}
AND wii.material_id =#{materialId}
AND wii.batch_code = #{batchCode}
AND date_format(wii.production_date, '%Y-%m-%d') = #{prodDate}
AND bs.putaway_move_line_no = #{putawayMoveLineNo}
order by bs.putaway_move_line_no DESC
order by bs.putaway_move_line_no ASC
