mapper.select:
select , ss.storage_code as storageCode, ss.storage_name as storageName,ss.storage_desc as storageDesc, sm.material_code as materialCode, sm.material_name as materialName, sm.material_version as materialVersion, sm.material_desc as materialDesc, sw.warehouse_name as warehouseName, swa.warehouse_area_name as warehouseAreaName, u.user_name as createUserName, s.user_name as modifiedUserName, bo.organization_name,bmo.material_owner_name as materialOwnerName from fantech_imes_v2.base_storage_material ssm left join fantech_imes_v2.base_storage ss on ssm.storage_id=ss.storage_id left join fantech_imes_v2.base_material sm on ssm.material_id=sm.material_id left join fantech_imes_v2.base_warehouse sw on ss.warehouse_id=sw.warehouse_id left join fantech_imes_v2.base_warehouse_area swa on ss.warehouse_area_id=swa.warehouse_area_id left join ocean_v2.sys_user u on ss.create_user_id=u.user_id left join ocean_v2.sys_user s on ss.modified_user_id=s.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON ssm.org_id = bo.organization_id LEFT JOIN base_material_owner bmo ON bmo.material_owner_id = ssm.material_owner_id
and date_format(ssm.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and sw.warehouse_id=#{warehouseId}
and bmo.material_owner_name like CONCAT('%', #{materialOwnerName}, '%')
and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and ss.warehouse_area_id=#{warehouseAreaId}
and swa.warehouse_area_name like CONCAT('%', #{warehouseAreaName}, '%')
and ssm.storage_id=#{storageId}
and ss.storage_code like CONCAT('%', #{storageCode}, '%')
and ss.storage_name like CONCAT('%', #{storageName}, '%')
and ssm.material_id=#{materialId}
and sm.material_code=#{materialCode}
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_desc like CONCAT('%', #{materialDesc}, '%')
and ssm.status=#{status}
and ssm.status = #{status}
and sm.material_name like CONCAT('%', #{materialName}, '%')
and sm.material_version like CONCAT('%', #{materialVersion}, '%')
and ssm.putaway_tactics = #{putawayTactics}
and ssm.replenish_tactics = #{replenishTactics}
and ssm.org_id=#{orgId}
order by ssm.create_time desc
