mapper.select:
select , u.user_name as createUserName, s.user_name as modifiedUserName, bo.organization_name from fantech_imes_v2.base_supplier ss left join ocean_v2.sys_user u on ss.create_user_id=u.user_id left join ocean_v2.sys_user s on ss.modified_user_id=s.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON ss.org_id = bo.organization_id
LEFT JOIN fantech_imes_v2.base_supplier_address ssa ON ssa.supplier_id = ss.supplier_id LEFT JOIN fantech_imes_v2.base_address sa ON sa.address_id = ssa.address_id
IF ((SELECT supplier_id FROM base_supplier_re_user WHERE user_id = #{userId} AND organization_id = #{orgId} LIMIT 1) IS NULL, 1=1,ss.supplier_id =(SELECT supplier_id FROM base_supplier_re_user WHERE user_id = #{userId} AND organization_id = #{orgId} LIMIT 1))
AND date_format(ss.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ss.supplier_code like CONCAT('%', #{supplierCode}, '%')
and ss.supplier_name like CONCAT('%', #{supplierName}, '%')
and ss.supplier_desc like CONCAT('%', #{supplierDesc}, '%')
and ss.supplier_type=#{supplierType}
and ss.supplier_id=#{supplierId}
and ss.status=#{status}
and sa.address_detail like CONCAT('%', #{addressDetail}, '%')
and ss.org_id=#{orgId}
order by ss.create_time desc
