mapper.select:
select , u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name,bs.supplier_code as supplierCode,bs.supplier_name as supplierName from base_supplier_re_user bsru LEFT JOIN base_supplier bs on bsru.supplier_id = bs.supplier_id left join sys_user u on bsru.create_user_id=u.user_id left join sys_user s on bsru.modified_user_id=s.user_id LEFT JOIN base_organization bo ON bsru.organization_id = bo.organization_id
and bsru.user_id = #{userId}
and bsru.supplier_id = #{supplierId}
and date_format(bsru.create_time, '%Y-%m-%d') BETWEEN date_format(#{startTime}, '%Y-%m-%d') AND date_format(#{endTime}, '%Y-%m-%d')
and bsru.status=#{status}
and bsru.organization_id=#{orgId}
order by create_time desc
