mapper.select:
select , su.user_name AS create_user_name, sus.user_name AS modified_user_name, qii.inspection_item_code,qii.inspection_item_name, qit.inspection_type_code,qit.inspection_type_name, sps.package_specification_code,sps.package_specification_name, spm.product_model_code,spm.product_model_name, blc.label_category_code,blc.label_category_name, bl.label_code,bl.label_name, ss.supplier_name,ss.supplier_code FROM fantech_imes.base_tab bt LEFT JOIN fantech_imes.qms_inspection_item qii ON bt.inspection_item_id = qii.inspection_item_id LEFT JOIN fantech_imes.qms_inspection_type qit ON bt.inspection_type_id = qit.inspection_type_id LEFT JOIN fantech_imes.smt_package_specification sps ON bt.package_specification_id = sps.package_specification_id LEFT JOIN fantech_imes.smt_product_model spm ON bt.product_model_id = spm.product_model_id LEFT JOIN fantech_imes.bcm_label_category blc ON bt.label_category_id = blc.label_category_id LEFT JOIN fantech_imes.bcm_label bl ON bt.label_id = bl.label_id LEFT JOIN fantech_imes.smt_supplier ss ON bt.supplier_id = ss.supplier_id LEFT JOIN ocean.sys_user su ON bt.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON bt.modified_user_id = sus.user_id
and date_format(bt.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and bt.material_id = #{materialId}
and bt.product_model_id = #{productModelId}
order by create_time desc
