mapper.select:
select , u.user_name as createUserName, s.user_name as modifiedUserName, bo.organization_name from fantech_imes_v2.base_warehouse sw left join ocean_v2.sys_user u on sw.create_user_id=u.user_id left join ocean_v2.sys_user s on sw.modified_user_id=s.user_id LEFT JOIN fantech_imes_v2.base_organization bo ON sw.org_id = bo.organization_id
and date_format(sw.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and sw.warehouse_code = #{warehouseCode}
and sw.warehouse_code like CONCAT('%', #{warehouseCode}, '%')
and sw.warehouse_name like CONCAT('%', #{warehouseName}, '%')
and sw.warehouse_desc like CONCAT('%', #{warehouseDesc}, '%')
and sw.status=#{status}
and sw.org_id=#{orgId}
and sw.warehouse_category like CONCAT('%', #{warehouseCategory}, '%')
order by sw.create_time desc

update.foreach:
update base_warehouse
warehouse_code =#{item.warehouseCode}, warehouse_name =#{item.warehouseName}, warehouse_desc =#{item.warehouseDesc}, warehouse_category =#{warehouseCategory}, link_man_name =#{item.linkManName}, link_man_phone =#{item.linkManPhone}, fax_number =#{item.faxNumber}, address =#{item.address}, sw.capacity =#{capacity}, sw.temperature =#{temperature}, sw.unit =#{unit}, status =#{item.status}, create_user_id =#{item.createUserId}, create_time =#{item.createTime}, modified_user_id =#{item.modifiedUserId}, modified_time =#{item.modifiedTime}, is_delete =#{item.isDelete}, option1 =#{item.option1}, option2 =#{item.option2}, option3 =#{item.option3}
where warehouse_code =#{item.warehouseCode}
