mapper.select:
select , su.user_name AS create_user_name, sus.user_name AS modified_user_name,bo.organization_name, spm.product_model_code AS customer_model, bmt.voltage, sm.material_code, sm.material_name from wanbao_barcode_rult_data wbrd LEFT JOIN ocean_v2.sys_user su ON wbrd.create_user_id = su.user_id LEFT JOIN ocean_v2.sys_user sus ON wbrd.modified_user_id = sus.user_id LEFT JOIN base_organization bo ON wbrd.org_id = bo.organization_id LEFT JOIN base_material sm ON wbrd.material_id = sm.material_id LEFT JOIN base_material_tab bmt on sm.material_id = bmt.material_id LEFT JOIN base_product_model spm ON bmt.product_model_id = spm.product_model_id
and sm.material_code like CONCAT('%', #{materialCode}, '%')
and sm.material_name like CONCAT('%', #{materialName}, '%')
and wbrd.identification_code like CONCAT('%', #{identificationCode}, '%')
and bmt.voltage like CONCAT('%', #{voltage}, '%')
and spm.product_model_code like CONCAT('%', #{customerModel}, '%')
and wbrd.data_status = #{dataStatus}
and wbrd.status=#{status}
and wbrd.org_id=#{orgId}
order by wbrd.identification_code
