mapper.select:
SELECT ,sw.warehouse_name as 'warehouseName',smc.material_category_name as 'materialCategoryName',sm.material_code as 'materialCode', sm.material_name as 'materialName',sm.material_desc as 'materialDesc',sm.version, su.user_name create_user_name,sus.user_name as modified_user_name, bo.organization_name FROM olt_ht_safe_stock os LEFT JOIN smt_warehouse sw ON os.warehouse_id=sw.warehouse_id LEFT JOIN smt_material_category smc ON os.material_category_id = smc.material_category_id LEFT JOIN smt_material sm ON os.material_id = sm.material_id LEFT JOIN ocean.sys_user su ON os.create_user_id = su.user_id LEFT JOIN ocean.sys_user sus ON os.modified_user_id = sus.user_id LEFT JOIN fantech_imes.base_organization bo ON os.organization_id = bo.organization_id
and sw.warehouse_id = #{warehouseId}
and sm.material_id = #{materialId}
and sm.material_desc like CONCAT('%',#{materialDesc},'%')
order by os.create_time desc
