mapper.select:
SELECT sum(ssi.quantity) FROM smt_storage_inventory ssi
LEFT JOIN smt_storage ss ON ssi.storage_id=ss.storage_id
LEFT JOIN smt_warehouse_area wa ON ss.warehouse_area_id =wa.warehouse_area_id
LEFT JOIN smt_warehouse sw ON wa.warehouse_id=sw.warehouse_id
LEFT JOIN base_tab bt ON ssi.material_id = bt.material_id
LEFT JOIN smt_material_category smc ON bt.material_type = smc.material_category_id


    and sw.warehouse_id = #{warehouseId}


    and smc.material_category_id= #{materialCategoryId}


    and ssi.material_id =#{materialId}
