mapper.select:
select , eeb.equipment_barcode, ee.equipment_code, ee.equipment_name, eepip.equ_point_inspection_project_code, eepip.equ_point_inspection_project_name, eec.equipment_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_equ_point_inspection_order eepio LEFT JOIN eam_equipment ee ON eepio.equipment_id = ee.equipment_id LEFT JOIN eam_equipment_category eec ON ee.equipment_category_id = eec.equipment_category_id LEFT JOIN eam_equipment_barcode eeb ON eepio.equipment_barcode_id = eeb.equipment_barcode_id LEFT JOIN eam_equ_point_inspection_project eepip ON eepio.equ_point_inspection_project_id = eepip.equ_point_inspection_project_id left join sys_user u on eepio.create_user_id=u.user_id left join sys_user s on eepio.modified_user_id=s.user_id left join base_organization bo ON eepio.org_id = bo.organization_id
and eepio.equipment_barcode_id = #{equipmentBarcodeId}
and eepip.equipment_category_id = #{equipmentCategoryId}
and eepio.order_status = #{orderStatus}
and eepio.equ_point_inspection_order_code like CONCAT('%', #{equPointInspectionOrderCode}, '%')
and eeb.equipment_barcode like CONCAT('%', #{equipmentBarcode}, '%')
and ee.equipment_code like CONCAT('%', #{equipmentCode}, '%')
and ee.equipment_name like CONCAT('%', #{equipmentName}, '%')
and eepio.status = #{status}
and eepio.org_id=#{orgId}
and #FULL_FROM_QUERY
order by eepio.create_time desc
