mapper.select:
select , ee.equipment_code,ee.equipment_name,ee.equipment_desc,ee.equipment_model, eec.equipment_category_name,eec.equipment_category_id, IF(eeb.current_usage_days is null,DATEDIFF(NOW(),eeb.create_time),DATEDIFF(NOW(),eeb.create_time)+eeb.current_usage_days) as current_usage_days, DATEDIFF(NOW(),eeb.last_time_maintain_time) as current_maintain_usage_days, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_equipment_barcode eeb left join fantech_imes_v2.eam_equipment ee on eeb.equipment_id=ee.equipment_id left join fantech_imes_v2.eam_equipment_category eec on ee.equipment_category_id=eec.equipment_category_id left join ocean_v2.sys_user u on eeb.create_user_id=u.user_id left join ocean_v2.sys_user s on eeb.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON eeb.org_id = bo.organization_id
and eeb.equipment_id = #{equipmentId}
and eeb.equipment_barcode like CONCAT('%', #{equipmentBarcode}, '%')
and eeb.equipment_barcode = #{equipmentBarcode}
and eeb.asset_code like CONCAT('%', #{assetCode}, '%')
and ee.equipment_code like CONCAT('%', #{equipmentCode}, '%')
and ee.equipment_name like CONCAT('%', #{equipmentName}, '%')
and ee.equipment_desc like CONCAT('%', #{equipmentDesc}, '%')
and ee.equipment_model like CONCAT('%', #{equipmentModel}, '%')
and date_format(eeb.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and eeb.status = #{status}
and eeb.org_id=#{orgId}
order by eeb.create_time desc
