mapper.select:
select , ee.equipment_id,ee.equipment_name, ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_equipment_jig_list eejl left join fantech_imes_v2.eam_equipment_jig eej ON eejl.equipment_jig_id = eej.equipment_jig_id left join fantech_imes_v2.eam_equipment ee on eej.equipment_id=ee.equipment_id left join fantech_imes_v2.eam_jig ej ON eejl.jig_id = ej.jig_id left join ocean_v2.sys_user u on eejl.create_user_id=u.user_id left join ocean_v2.sys_user s on eejl.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON eejl.org_id = bo.organization_id
and eejl.equipment_jig_id = #{equipmentJigId}
and eejl.jig_id = #{jigId}
and date_format(eejl.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and eejl.status = #{status}
and eejl.org_id=#{orgId}
order by eejl.create_time desc

select , ee.equipment_id,ee.equipment_code,ee.equipment_name,ee.equipment_desc,ee.equipment_model, ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_equipment_jig_list eejl left join fantech_imes_v2.eam_equipment_jig eej ON eejl.equipment_jig_id = eej.equipment_jig_id left join fantech_imes_v2.eam_equipment ee on eej.equipment_id=ee.equipment_id left join fantech_imes_v2.eam_jig ej ON eejl.jig_id = ej.jig_id left join ocean_v2.sys_user u on eejl.create_user_id=u.user_id left join ocean_v2.sys_user s on eejl.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON eejl.org_id = bo.organization_id
and eej.equipment_jig_id = #{equipmentJigId}
and ee.equipment_code like CONCAT('%', #{equipmentCode}, '%')
and ee.equipment_name like CONCAT('%', #{equipmentName}, '%')
and ee.equipment_desc like CONCAT('%', #{equipmentDesc}, '%')
and ee.equipment_model like CONCAT('%', #{equipmentModel}, '%')
and date_format(eej.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and eej.status = #{status}
and eej.org_id=#{orgId}
order by eejl.create_time desc
