mapper.select:
select , eeb.equipment_barcode, ee.equipment_code, ee.equipment_name, eemp.equipment_maintain_project_code, eemp.equipment_maintain_project_name, eec.equipment_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_equipment_maintain_order eemo LEFT JOIN eam_equipment ee ON eemo.equipment_id = ee.equipment_id LEFT JOIN eam_equipment_category eec ON ee.equipment_category_id = eec.equipment_category_id LEFT JOIN eam_equipment_barcode eeb ON eemo.equipment_barcode_id = eeb.equipment_barcode_id LEFT JOIN eam_equipment_maintain_project eemp ON eemo.equipment_maintain_project_id = eemp.equipment_maintain_project_id LEFT JOIN sys_user u ON eemo.create_user_id = u.user_id LEFT JOIN sys_user s ON eemo.modified_user_id = s.user_id LEFT JOIN base_organization bo ON eemo.org_id = bo.organization_id
and ee.equipment_category_id = #{equipmentCategoryId}
and eemo.equipment_barcode_id = #{equipmentBarcodeId}
and eemo.order_status = #{orderStatus}
and eemo.equipment_maintain_order_code like CONCAT('%', #{equipmentMaintainOrderCode}, '%')
and eeb.equipment_barcode like CONCAT('%', #{equipmentBarcode}, '%')
and ee.equipment_code like CONCAT('%', #{equipmentCode}, '%')
and ee.equipment_name like CONCAT('%', #{equipmentName}, '%')
and eemo.status = #{status}
and eemo.org_id=#{orgId}
and #FULL_FROM_QUERY
order by eemo.create_time desc
