mapper.select:
select , eeb.equipment_barcode,eeb.asset_code, ee.equipment_code,ee.equipment_name,ee.equipment_desc,ee.equipment_model, eesb.property_code_category,eec.equipment_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_equipment_scrap_order_det eesod left join fantech_imes_v2.eam_equipment_barcode eeb on eesod.equipment_barcode_id=eeb.equipment_barcode_id left join fantech_imes_v2.eam_equipment_standing_book eesb on eesod.equipment_barcode_id=eesb.equipment_barcode_id left join fantech_imes_v2.eam_equipment ee on eeb.equipment_id=ee.equipment_id left join fantech_imes_v2.eam_equipment_category eec on ee.equipment_category_id=eec.equipment_category_id left join ocean_v2.sys_user u on eesod.create_user_id=u.user_id left join ocean_v2.sys_user s on eesod.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON eesod.org_id = bo.organization_id
and eesod.equipment_scrap_order_id = #{equipmentScrapOrderId}
and date_format(eesod.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and eesod.status = #{status}
and eesod.org_id=#{orgId}
order by eesod.create_time desc
