mapper.select:
select ,
bw.warehouse_name,
bwa.warehouse_area_name,
bs.storage_code,
bs.storage_name,
bwo.working_area_code,
u.user_name as create_user_name,
s.user_name as modified_user_name,
bo.organization_name
from fantech_imes_v2.eam_ht_equipment_backup eeb
left join fantech_imes_v2.base_warehouse bw on eeb.warehouse_id=bw.warehouse_id
left join fantech_imes_v2.base_warehouse_area bwa on eeb.warehouse_area_id=bwa.warehouse_area_id
left join fantech_imes_v2.base_working_area bwo on eeb.working_area_id=bwo.working_area_id
left join fantech_imes_v2.base_storage bs on eeb.storage_id=bs.storage_id
left join ocean_v2.sys_user u on eeb.create_user_id=u.user_id
left join ocean_v2.sys_user s on eeb.modified_user_id=s.user_id
left join fantech_imes_v2.base_organization bo ON eeb.org_id = bo.organization_id


    and eeb.equipment_backup_code = #{equipmentBackupCode}


    and eeb.equipment_backup_name = #{equipmentBackupName}


    and eeb.equipment_backup_desc = #{equipmentBackupDesc}


    and eeb.equipment_backup_model = #{equipmentBackupModel}


    and eeb.equipment_id = #{equipmentId}


    and date_format(eeb.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}


    and eeb.status = #{status}


    and eeb.org_id=#{orgId}


order by eeb.create_time desc
