mapper.select:
select , ee.equipment_code,ee.equipment_name,ee.equipment_desc,ee.equipment_model, bbc.badness_cause_code,bbc.badness_cause_desc, bbd.badness_duty_code,bbd.badness_duty_desc, bbp.badness_phenotype_code,bbp.badness_phenotype_desc, su.user_name as repair_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_equipment_repair_order ehero left join fantech_imes_v2.eam_equipment ee on ehero.equipment_id = ee.equipment_id left join fantech_imes_v2.base_badness_cause bbc on ehero.badness_cause_id = bbc.badness_cause_id left join fantech_imes_v2.base_badness_duty bbd on ehero.badness_duty_id = bbd.badness_duty_id left join fantech_imes_v2.base_badness_phenotype bbp on ehero.badness_phenotype_id = bbp.badness_phenotype_id left join ocean_v2.sys_user su on ehero.repair_user_id=su.user_id left join ocean_v2.sys_user u on ehero.create_user_id=u.user_id left join ocean_v2.sys_user s on ehero.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehero.org_id = bo.organization_id
and ehero.equipment_repair_order_id = #{equipmentRepairOrderId}
and ehero.equipment_repair_order_code like CONCAT('%', #{equipmentRepairOrderCode}, '%')
and ehero.equipment_barcode like CONCAT('%', #{equipmentBarcode}, '%')
and ee.equipment_code like CONCAT('%', #{equipmentCode}, '%')
and ee.equipment_name like CONCAT('%', #{equipmentName}, '%')
and ee.equipment_model like CONCAT('%', #{equipmentModel}, '%')
and ehero.order_status = #{orderStatus}
and ehero.equipment_barcode_id = #{equipmentBarcodeId}
and date_format(ehero.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehero.status = #{status}
and ehero.org_id=#{orgId}
order by ehero.create_time desc
