mapper.select:
select , eeb.equipment_barcode,eeb.asset_code, ee.equipment_code,ee.equipment_name,ee.equipment_desc,ee.equipment_model, eesb.property_code_category,eec.equipment_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_ht_equipment_scrap_order_det ehesod left join eam_equipment_barcode eeb on ehesod.equipment_barcode_id=eeb.equipment_barcode_id left join eam_equipment_standing_book eesb on ehesod.equipment_barcode_id=eesb.equipment_barcode_id left join eam_equipment ee on eeb.equipment_id=ee.equipment_id left join eam_equipment_category eec on ee.equipment_category_id=eec.equipment_category_id left join sys_user u on ehesod.create_user_id=u.user_id left join sys_user s on ehesod.modified_user_id=s.user_id left join base_organization bo ON ehesod.org_id = bo.organization_id
and ehesod.equipment_scrap_order_id = #{equipmentScrapOrderId}
and date_format(ehesod.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehesod.status = #{status}
and ehesod.org_id=#{orgId}
order by ehesod.create_time desc
