mapper.select:
select , bd.dept_name as apply_dept_name, d.dept_name as use_dept_name, su.user_name as apply_user_name, sus.user_name as use_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_ht_equipment_scrap_order eheso left join base_dept d on eheso.use_dept_id=d.dept_id left join base_dept bd on eheso.apply_dept_id=bd.dept_id left join sys_user su on eheso.apply_user_id=su.user_id left join sys_user sus on eheso.use_user_id=sus.user_id left join sys_user u on eheso.create_user_id=u.user_id left join sys_user s on eheso.modified_user_id=s.user_id left join base_organization bo ON eheso.org_id = bo.organization_id
and eheso.equipment_scrap_order_code like CONCAT('%', #{equipmentScrapOrderCode}, '%')
and bd.dept_name like CONCAT('%', #{applyDeptName}, '%')
and d.dept_name like CONCAT('%', #{useDeptName}, '%')
and su.user_name like CONCAT('%', #{applyUserName}, '%')
and sus.user_name like CONCAT('%', #{useUserName}, '%')
and eheso.order_status = #{orderStatus}
and date_format(eheso.apply_time, '%Y-%m-%d') = #{applyTime}
and date_format(eheso.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and eheso.status = #{status}
and eheso.org_id=#{orgId}
order by eheso.create_time desc
