mapper.select:
select , ejb.jig_barcode,ej.jig_code,ej.jig_name,ejc.jig_category_name, ejmp.jig_maintain_project_code,ejmp.jig_maintain_project_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_ht_jig_maintain_order ehjmo left join eam_jig_maintain_project ejmp on ehjmo.jig_maintain_project_id=ejmp.jig_maintain_project_id left join eam_jig ej on ehjmo.jig_id=ej.jig_id left join eam_jig_category ejc on ej.jig_category_id=ejc.jig_category_id left join eam_jig_barcode ejb on ehjmo.jig_barcode_id=ejb.jig_barcode_id left join sys_user u on ehjmo.create_user_id=u.user_id left join sys_user s on ehjmo.modified_user_id=s.user_id left join base_organization bo ON ehjmo.org_id = bo.organization_id
and ehjmo.jig_maintain_order_code like CONCAT('%', #{jigMaintainOrderCode}, '%')
and ejb.jig_barcode like CONCAT('%', #{jigBarcode}, '%')
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ejc.jig_category_name like CONCAT('%', #{jigCategoryName}, '%')
and ehjmo.order_status = #{orderStatus}
and date_format(ehjmo.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjmo.status = #{status}
and ehjmo.org_id=#{orgId}
order by ehjmo.create_time desc
