mapper.select:
select , ejpipi.jig_point_inspection_project_item_name,ejpipi.jig_point_inspection_project_item_desc, ejpipi.judge_type,ejpipi.ration_floor,ejpipi.ration_upper_limit, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_jig_point_inspection_order_det ehjpiod left join fantech_imes_v2.eam_jig_point_inspection_project_item ejpipi on ehjpiod.jig_point_inspection_project_item_id=ejpipi.jig_point_inspection_project_item_id left join ocean_v2.sys_user u on ehjpiod.create_user_id=u.user_id left join ocean_v2.sys_user s on ehjpiod.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehjpiod.org_id = bo.organization_id
and ehjpiod.jig_point_inspection_order_id = #{jigPointInspectionOrderId}
and date_format(ehjpiod.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjpiod.status = #{status}
and ehjpiod.org_id=#{orgId}
order by ehjpiod.create_time desc
