mapper.select:
select , ejb.jig_barcode,ej.jig_code,ej.jig_name,ejc.jig_category_name, ejpip.jig_point_inspection_project_code,ejpip.jig_point_inspection_project_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from eam_ht_jig_point_inspection_order ehjpio left join eam_jig_point_inspection_project ejpip on ehjpio.jig_point_inspection_project_id=ejpip.jig_point_inspection_project_id left join eam_jig ej on ehjpio.jig_id=ej.jig_id left join eam_jig_category ejc on ej.jig_category_id=ejc.jig_category_id left join eam_jig_barcode ejb on ehjpio.jig_barcode_id=ejb.jig_barcode_id left join sys_user u on ehjpio.create_user_id=u.user_id left join sys_user s on ehjpio.modified_user_id=s.user_id left join base_organization bo ON ehjpio.org_id = bo.organization_id
and ehjpio.jig_point_inspection_order_code like CONCAT('%', #{jigPointInspectionOrderCode}, '%')
and ejb.jig_barcode like CONCAT('%', #{jigBarcode}, '%')
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ejc.jig_category_name like CONCAT('%', #{jigCategoryName}, '%')
and ehjpio.order_status = #{orderStatus}
and date_format(ehjpio.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjpio.status = #{status}
and ehjpio.org_id=#{orgId}
order by ehjpio.create_time desc
