mapper.select:
select , ejc.jig_category_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_jig_point_inspection_project ehjpip left join fantech_imes_v2.eam_jig_category ejc ON ehjpip.jig_category_id = ejc.jig_category_id left join ocean_v2.sys_user u on ehjpip.create_user_id=u.user_id left join ocean_v2.sys_user s on ehjpip.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehjpip.org_id = bo.organization_id
and ehjpip.jig_category_id = #{jigCategoryId}
and ehjpip.jig_point_inspection_project_code like CONCAT('%', #{jigPointInspectionProjectCode}, '%')
and ehjpip.jig_point_inspection_project_name like CONCAT('%', #{jigPointInspectionProjectName}, '%')
and ehjpip.jig_point_inspection_project_desc like CONCAT('%', #{jigPointInspectionProjectDesc}, '%')
and ejc.jig_category_name like CONCAT('%', #{jigCategoryName}, '%')
and date_format(ehjpip.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjpip.status = #{status}
and ehjpip.org_id=#{orgId}
order by ehjpip.create_time desc
