mapper.select:
select , ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model, bbc.badness_cause_code,bbc.badness_cause_desc, bbd.badness_duty_code,bbd.badness_duty_desc, bbp.badness_phenotype_code,bbp.badness_phenotype_desc, su.user_name as repair_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_jig_repair_order ehjro left join fantech_imes_v2.eam_jig ej on ehjro.jig_id = ej.jig_id left join fantech_imes_v2.base_badness_cause bbc on ehjro.badness_cause_id = bbc.badness_cause_id left join fantech_imes_v2.base_badness_duty bbd on ehjro.badness_duty_id = bbd.badness_duty_id left join fantech_imes_v2.base_badness_phenotype bbp on ehjro.badness_phenotype_id = bbp.badness_phenotype_id left join ocean_v2.sys_user su on ehjro.repair_user_id=su.user_id left join ocean_v2.sys_user u on ehjro.create_user_id=u.user_id left join ocean_v2.sys_user s on ehjro.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehjro.org_id = bo.organization_id
and ehjro.jig_repair_order_code like CONCAT('%', #{jigRepairOrderCode}, '%')
and ehjro.jig_barcode like CONCAT('%', #{jigBarcode}, '%')
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ej.jig_model like CONCAT('%', #{jigModel}, '%')
and date_format(ehjro.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjro.status = #{status}
and ehjro.org_id=#{orgId}
order by ehjro.create_time desc
