mapper.select:
select , ej.jig_code,ej.jig_name,ej.jig_desc,ej.jig_model,ejc.jig_category_name, ejb.jig_barcode,wo.work_order_code,isnull(ejret.jig_return_id) as requisition_status, ehjr.create_time as requisition_time,ejret.create_time as return_time,ejret.this_time_usage_time, bw.warehouse_name,bwa.warehouse_area_name,bs.storage_code, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_jig_requisition ehjr left join fantech_imes_v2.eam_jig_return ejret on ehjr.jig_requisition_id=ejret.jig_requisition_id left join fantech_imes_v2.eam_jig ej on ehjr.jig_id=ej.jig_id left join fantech_imes_v2.base_warehouse bw on ej.warehouse_id=bw.warehouse_id left join fantech_imes_v2.base_warehouse_area bwa on ej.warehouse_area_id=bwa.warehouse_area_id left join fantech_imes_v2.base_storage bs on ej.storage_id=bs.storage_id left join fantech_imes_v2.eam_jig_category ejc on ej.jig_category_id=ejc.jig_category_id left join fantech_imes_v2.eam_jig_barcode ejb on ehjr.jig_barcode_id=ejb.jig_barcode_id left join fantech_imes_v2.mes_pm_work_order wo on ehjr.work_order_id=wo.work_order_id left join ocean_v2.sys_user u on ehjr.create_user_id=u.user_id left join ocean_v2.sys_user s on ehjr.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehjr.org_id = bo.organization_id
and isnull(ejret.jig_return_id)=#{requisitionStatus}
and wo.jig_id = #{jigId}
and wo.work_order_id = #{workOrderId}
and wo.work_order_code like CONCAT('%', #{workOrderCode}, '%')
and ejb.jig_barcode like CONCAT('%', #{jigBarcode}, '%')
and ej.jig_code like CONCAT('%', #{jigCode}, '%')
and ej.jig_name like CONCAT('%', #{jigName}, '%')
and ej.jig_model like CONCAT('%', #{jigModel}, '%')
and ejc.jig_category_name like CONCAT('%', #{jigCategoryName}, '%')
and date_format(ehjr.create_time, '%Y-%m-%d') = #{requisitionTime}
and date_format(ejret.create_time, '%Y-%m-%d') = #{returnTime}
and date_format(ehjr.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjr.status = #{status}
and ehjr.org_id=#{orgId}
order by ehjr.create_time desc
