mapper.select:
select , bd.dept_name as apply_dept_name, d.dept_name as use_dept_name, su.user_name as apply_user_name, sus.user_name as use_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_jig_scrap_order ehjso left join fantech_imes_v2.base_dept d on ehjso.use_dept_id=d.dept_id left join fantech_imes_v2.base_dept bd on ehjso.apply_dept_id=bd.dept_id left join ocean_v2.sys_user su on ehjso.apply_user_id=su.user_id left join ocean_v2.sys_user sus on ehjso.use_user_id=sus.user_id left join ocean_v2.sys_user u on ehjso.create_user_id=u.user_id left join ocean_v2.sys_user s on ehjso.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehjso.org_id = bo.organization_id
and ehjso.jig_scrap_order_code like CONCAT('%', #{jigScrapOrderCode}, '%')
and bd.dept_name like CONCAT('%', #{applyDeptName}, '%')
and d.dept_name like CONCAT('%', #{useDeptName}, '%')
and su.user_name like CONCAT('%', #{applyUserName}, '%')
and sus.user_name like CONCAT('%', #{useUserName}, '%')
and ehjso.order_status = #{orderStatus}
and date_format(ehjso.apply_time, '%Y-%m-%d') = #{applyTime}
and date_format(ehjso.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehjso.status = #{status}
and ehjso.org_id=#{orgId}
order by ehjso.create_time desc
