mapper.select:
select , bf.factory_name,bws.work_shop_name,bpl.pro_name,su.user_name as audit_user_name, u.user_name as create_user_name, s.user_name as modified_user_name, bo.organization_name from fantech_imes_v2.eam_ht_news ehn left join fantech_imes_v2.base_factory bf on ehn.factory_id=bf.factory_id left join fantech_imes_v2.base_work_shop bws on ehn.work_shop_id=bws.work_shop_id left join fantech_imes_v2.base_pro_line bpl on ehn.pro_line_id=bpl.pro_line_id left join ocean_v2.sys_user su on ehn.audit_user_id=su.user_id left join ocean_v2.sys_user u on ehn.create_user_id=u.user_id left join ocean_v2.sys_user s on ehn.modified_user_id=s.user_id left join fantech_imes_v2.base_organization bo ON ehn.org_id = bo.organization_id
and ehn.news_code like CONCAT('%', #{newsCode}, '%')
and ehn.news_title like CONCAT('%', #{newsTitle}, '%')
and bf.factory_name like CONCAT('%', #{factoryName}, '%')
and bws.work_shop_name like CONCAT('%', #{workShopName}, '%')
and bpl.pro_name like CONCAT('%', #{proName}, '%')
and ehn.new_status = #{newStatus}
and date_format(ehn.create_time, '%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
and ehn.status = #{status}
and ehn.org_id=#{orgId}
order by ehn.create_time desc
